import React from 'react';
import './App.css';
import Header from './components/header.js';
import Data from './components/info/data.js';




class App extends Component {
  constructor() {
    super();

    this.state = {
      'bio' : 'Max',
      'name' : 'Liam'
    }
  }

  incrementAge = () => {
    let current_age = this.state.age;

    this.setState(
      { 'age' : current_age + 1 }
    );
  }

  decrementAge = () => {
    let current_age = this.state.age;

    this.setState(
      { 'age' : current_age - 1 }
    );
  }


  render() {
    return (
      <div className="App">
        <h1>{this.props.class}</h1>
        <Data sport={"Baseball"} team={'Red Sox'} app_name={this.state.name} incrementAge={this.incrementAge} decrementAge={this.decrementAge}/>
        <p>{this.state.name} is {this.state.age} years old.</p>
      </div>
    );
  }
}

export default App;
