import React, { Component } from 'react';

class Data extends Component {
  constructor() {
    super();

    this.state = {
      'name': 'John Smith',
      'age': 22
    };
  }

  updateAge = () => {
    let age = this.state.age;

    this.setState(
      { 'age' : age + 1 }
    );

    this.props.incrementAge();
  }

  updateAge1 = () => {
    let age = this.state.age;

    this.setState(
      { 'age' : age - 1 }
    );

    this.props.decrementAge();
  }

  render() {
    // this is JavaScript
    return ( // this is also JavaScript
      <div className="Info">

        <p>{this.state.name} is {this.state.age} years old.</p>
        <p>{this.props.team} play the sport of {this.props.sport}.</p>
        {/* This is a comment in JSX */}
        <p>This is the info component.</p>
        <button onClick={() => this.updateAge()}>
          Gain a life!
        </button>
        <br />
        <br />
        <button onClick={() => this.updateAge1()}>
          Lose a life!
        </button>
      </div> // this is also JavaScript
    );
  }
}

export default Data;
